/* Global Variables for addresses of buffer */
volatile int pixel_buffer_start;
volatile int* pixel_ctrl_ptr = (int*)PIXEL_BUF_CTRL_BASE; // addr of front buffer reg
volatile char * char_buffer = (char *)FPGA_CHAR_BASE; // addr of character buffer base

int main(void) {
    *(pixel_ctrl_ptr + 1) = FPGA_ONCHIP_BASE; // first store the address in the back buffer
    //now, swap the front/back buffers, to set the front buffer location
    wait_for_vsync();
	
	play = true;
	
	// randomize the x-direction of the ball
	dx_ball = (((rand() % 2) * 2) - 1) * 2;
	
	printf("%d\n", dx_ball);
	
    //initialize a pointer to the pixel buffer, used by drawing functions
    pixel_buffer_start = *pixel_ctrl_ptr;
    clear_screen(); // pixel_buffer_start points to the pixel buffer
	
    //set back pixel buffer to start of SDRAM memory
    *(pixel_ctrl_ptr + 1) = SDRAM_BASE;
    pixel_buffer_start = *(pixel_ctrl_ptr + 1); // we draw on the back buffer
	clear_screen(); // pixel_buffer_start points to the pixel buffer
	
	wait_for_vsync();
	
	//Display "Press ENTER to start"
	draw_text(40-10, 10, start_msg);
	
	while (play) {
		//Display ball and players
		draw_ball();
		draw_plr();
		
		//If pressed enter, clear the message and start
		if (start) clear_text(40-10, 10, start_msg);
		
		wait_for_vsync();
		pixel_buffer_start = *(pixel_ctrl_ptr + 1); // new back buffer
	}
	
	return 0;
}

/* Function for plotting a single pixel */
void plot_pixel(int x, int y, short int color) {
    *(short int *)(pixel_buffer_start + (y << 10) + (x << 1)) = color;
}

/* Function for clearing the screen */
void clear_screen() {
	for (int x = 0; x < RESOLUTION_X; x++) {
		for (int y = 0; y < RESOLUTION_Y; y++) plot_pixel(x, y, BG);
	}
	
	if (play) {
		draw_rectangle(156, 140, 6, 100, GREY); //net
		draw_rectangle(0, 230, 340, 10, WHITE); //ground
	}
	
	//center guidelines
	//draw_rectangle(159, 0, 1, 240, RED);
	//draw_rectangle(0, 119, 320, 1, RED);
}

/* Function for clearing the text */
void clear_text(int x, int y, char* text) {
	int offset = (y << 7) + x;
	
	while (*(text)) {
		*(char_buffer + offset) = ' '; //write to the character buffer
		text++;
		offset++;
	}
}

/* Function to wait for vsync */
void wait_for_vsync() {
	//volatile int* pixel_ctrl_ptr = (int*)0xFF203020;
	int status;
	//lauch swap process
	*pixel_ctrl_ptr = 1; //sets S = 1
	//poll status bit
	status = *(pixel_ctrl_ptr + 3); //dereference status reg
	while ((status & 0x01)!=0) {
		status = *(pixel_ctrl_ptr+3);
	}
	// exit when status bit S = 0
}

